import React, { useState, useEffect } from "react";

import {
  Route,
  Switch,
  Redirect,
  useHistory,
  useLocation
} from "react-router-dom";

import Get from "./Get";
import Login from "./Login";
import Post from "./Post";
import NotFound from "../NotFound";

function AdminApp(props) {
  const [isAutheticated, setisAutheticated] = useState(false);

  const auth = user => {
    if (user) setisAutheticated(true);
    else setisAutheticated(false);
  };

  useEffect(() => {
    props.isAdminChange();
    return () => {
      props.isAdminChange();
    }; //eslint-disable-next-line
  }, []);

  return (
    <>
      <Switch>
        <Route path="/admin" exact>
          <Login auth={auth}></Login>
        </Route>
        <Route path="/admin/get" exact>
          <Get></Get>
        </Route>
        <Route path="/admin/post">
          <Post></Post>
        </Route>
        <Route>
          <NotFound></NotFound>
        </Route>
      </Switch>
    </>
  );
}

function PrivateRoute({ children, ...rest }) {
  return (
    <Route
      {...rest}
      render={({ location }) =>
        isAuthenticated ? (
          children
        ) : (
          <Redirect
            to={{
              pathname: "/admin",
              state: { from: location }
            }}
          />
        )
      }
    />
  );
}

export default AdminApp;
