import React, { useState, useEffect } from "react";

import {
  Route,
  Switch,
  Redirect,
  useHistory,
  useLocation
} from "react-router-dom";

import Get from "./Get";
import Login from "./Login";
import Post from "./Post";
import NotFound from "../NotFound";
import PrivateRoute from "./PrivateRoute";

function AdminApp(props) {
  const [isAutheticated, setisAutheticated] = useState(false);

  const auth = user => {
    if (user) setisAutheticated(true);
    else setisAutheticated(false);
  };

  useEffect(() => {
    props.isAdminChange();
    return () => {
      props.isAdminChange();
    }; //eslint-disable-next-line
  }, []);

  return (
    <>
      <Switch>
        <Route path="/admin" exact>
          <Login auth={auth}></Login>
        </Route>
        <PrivateRoute path="/admin/auth/get" isAutheticated={isAutheticated}>
          <Get></Get>
        </PrivateRoute>
        <PrivateRoute path="/admin/auth/post" isAutheticated={isAutheticated}>
          <Post></Post>
        </PrivateRoute>
        <Route>
          <NotFound></NotFound>
        </Route>
      </Switch>
    </>
  );
}

export default AdminApp;
