import React, { useState, useEffect } from "react";
import axios from "axios";
import Grid from "@material-ui/core/Grid";
import CustomCard from "../components/CustomCard";
import { Link } from "react-router-dom";
function Develop() {
  const [contents, setContents] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchContents = async () => {
      try {
        // 요청이 시작 할 때에는 error 와 users 를 초기화하고
        setError(null);
        setContents(null);
        // loading 상태를 true 로 바꿉니다.
        setLoading(true);
        const response = await axios.get(
          "/categories/5de6490186bfe46f94f28c75/articles/"
        );
        setContents([...response.data.data]); // 데이터는 response.data 안에 들어있습니다.
        console.log(response.data.data);
      } catch (e) {
        setError(e);
      }
      setLoading(false);
    };
    fetchContents();
  }, []);

  if (loading) return <div>로딩중..</div>;
  if (error) return <div>{error}</div>;
  if (!contents) return null;

  // const contentLen = contents.length;

  return (
    <Grid container>
      {contents.map((content, index) => (
        <Link to="/daily" className={classes.link}>
          <CustomCard
            key={index}
            xs={12}
            contentId={content._id}
            thumbnail={content.thumbnail}
            articlename={content.articlename}
            contents={content.contents}
            createdAt={content.createdAt.substring(0, 10)}
            width={"1000px"}
          ></CustomCard>
        </Link>
      ))}
    </Grid>
  );
}

export default Develop;
